	$pattern_short = '{<li>((?:(?:(?!<li[^>]*>|</li>).)++|<li[^>]*>(?1)</li>)*)</li>}si';
	
		preg_match_all($pattern_short,  $html, $out, PREG_PATTERN_ORDER);
		//$r = print_r ($out, true);
		
		//print_r($out);
		
		
		
		foreach ($out[1] as $o)
		{
			
			if (preg_match('/<span style=\'font-variant: small-caps;\'>(?<authors>.*)<\/span>. (?<year>[0-9]{4}). (?<atitle>.*).\s*Breviora, <strong>(?<volume>[0-9]+)<\/strong>:(?<spage>[0-9]+)-(?<epage>[0-9]+)\s*\[<a href=\'(?<url>(.*))\'>BHL<\/a>\]/', $o, $matches))
			{
				//print_r($matches);
				
				$obj = new stdclass;
				
				$obj->title = 'Breviora';
				$obj->issn = '0006-9698';
				$obj->volume = $matches['volume'];
				$obj->spage = $matches['spage'];
				$obj->epage = $matches['epage'];
				
				$obj->atitle = trim(strip_tags($matches['atitle']));
				$obj->atitle = preg_replace('/\.$/', '', $obj->atitle);
				
				$obj->year = $matches['year'];
				$obj->url = $matches['url'];
				
				$author_str = $matches['authors'];
				$author_str = str_replace("&", "|", $author_str);
				// Strip Jr
				$author_str = str_replace(", Jr", "", $author_str);
				$author_str = str_replace(" and ", "|", $author_str);
				$author_str = str_replace(".,", "|", $author_str);
				$author_str = str_replace(";", "|", $author_str);
				$obj->authors = explode("|", $author_str);
				
				//print_r($obj);
				
				// RIS dump
				$export = "TY  - JOUR\n";
//				$export .= "ID  - " . $obj->publisher_id . "\n";
				foreach ($obj->authors as $author)
				{
					$author = trim($author);
					$author = preg_replace('/\,$/', '', $author);
					$export .= "A1  - " . trim($author) . "\n";
				}		
				$export .= "T1  - " . $obj->atitle . "\n";
				$export .= "JF  - " . $obj->title . "\n";
				$export .= "SN  - " . $obj->issn . "\n";
				$export .= "VL  - " . $obj->volume . "\n";
//				$export .= "IS  - " . $obj->issue . "\n";
				$export .= "SP  - " . $obj->spage . "\n";
				$export .= "EP  - " . $obj->epage . "\n";
				$export .= "Y1  - " . $obj->year . "\n";
				$export .= "L2  - " . $obj->url . "\n";
				$export .= "ER  - \n\n";
				
				echo $export;
				
			}
			
			
			
